    public float cgRatio(String dna){
        // Count the c and g tiimes.
        int cCount = 0, gCount = 0;
        // Record the c and g current index.
        int cIndex = 0, gIndex = 0;
        cIndex = dna.indexOf("C", cIndex);
        while(cIndex != -1){
            cCount++;
            cIndex = dna.indexOf("C", cIndex+1);
        }
        while(gIndex != -1){
            gCount++;
            gIndex = dna.indexOf("G", gIndex+1);
        }
        return (float)cCount/gCount;
    }
    
    public void testcgRatio(){
        String dna = "ATGCCATAG";
        System.out.println(cgRatio(dna));
    }
    
    public int countCTG(String dna){
        int count = 0;
        int currIndex = dna.indexOf("CTG", 0);
        while(currIndex != -1){
            count++;
            currIndex = dna.indexOf("CTG", currIndex+3);
        }
        return count;
    }
    
