    public void processGenes(StorageResource sr){
        StorageResource genes = new StorageResource();
        int numgene = 0;
        StorageResource cgratiogene = new StorageResource();
        int cgratio = 0;
        int longestgene = 0;
        
        for(String gene: sr.data()){
            int lenGene = gene.length();
            if (cgRatio(gene) > 0.35){
                cgratio++;
                cgratiogene.add(gene);
            }
            if (lenGene > 60){
                numgene++;
                genes.add(gene);
            }
            if(lenGene > longestgene){
                longestgene = lenGene;
            }
        }
        System.out.println("Length greater than 60 has " + numgene);
        for(String gene: genes.data()){
            System.out.println(gene);
        }
        System.out.println("C-G-ratio is higher than 0.35 has " + cgratio);
        for(String gene: cgratiogene.data()){
            System.out.println(gene);
        }
        System.out.println("The longest Gene's length is " + longestgene);
        System.out.println("================================================");
    }
    
    public StorageResource getAllGenes(String dna){
        StorageResource sr = new StorageResource();
        int startIndex = dna.indexOf("ATG", 0);
        if(startIndex == -1) return sr;
        int currIndex = 0;
        while(currIndex != -1){
            int TAAIndex = findStopCodon(dna, startIndex, "TAA");
            int TAGIndex = findStopCodon(dna, startIndex, "TAG");
            int TGAIndex = findStopCodon(dna, startIndex, "TGA");
            currIndex = Math.min(TAAIndex, Math.min(TAGIndex, TGAIndex));
            if(currIndex == dna.length()) break;
            String gene = dna.substring(startIndex, currIndex+3);
            sr.add(gene);
            startIndex = dna.indexOf("ATG", currIndex+1);
        }
        return sr;
    }
    
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int currIndex = dna.indexOf(stopCodon, startIndex+3);
        // Doesn't Find anything
        if(currIndex == -1) return dna.length();
        int diffIndex = currIndex - startIndex;
        while(true){
            if(diffIndex % 3 == 0) return currIndex;
            currIndex = dna.indexOf(stopCodon, startIndex+currIndex+1);
            if(currIndex == -1) break;
        }
        
        return dna.length();
    }
    
    public void testStopCodon(){
        String dna = "AAAATGTAATGATAA";
        int ans = findStopCodon(dna, dna.indexOf("ATG"), "TGA");
        System.out.println(ans);
    }
    
